(59, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2023-11-07 18:48:20', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2023-11-07 19:47:31', NULL, 0, 'plain', '', 0, 1, 'info'),
(61, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2023-11-07 18:48:20', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-11-07 18:48:20', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'wc_admin_WooPay_Oct30_2023', 'marketing', 'en_US', 'Convert more sales with our fastest checkout yet', 'Enable WooPay and offer the only single-click checkout experience built by Woo, for Woo. Give shoppers the power to pay instantly and securely speed through checkout, even if it\'s their first visit. Add it for free with one tap in your WooPayments dashboard.', '{}', 'pending', 'woocommerce.com', '2023-11-07 18:48:20', NULL, 0, 'plain', '', 0, 0, 'info');
